import Vue from 'vue';
import Router from 'vue-router';
import About from '../modules/about/index.vue';
import Home from '../modules/home/index.vue';
import Page404 from "../modules/errors/Page404.vue";

Vue.use(Router);

export function createRouter() {
    return new Router({
        mode: 'history',
        routes: [
            {
                path: '/',
                component: Home,
                name: 'home'
            },
            {
                path: '/about/:id',
                component: About,
                name: 'about'
            },
            {path: "*", component: Page404}
        ]
    });
};

import {Store} from 'vuex';

const createStore = () => {
    return new Store({
        strict: process.env.NODE_ENV !== 'production',
    });
};

export default createStore;

import Vue from 'vue';
import Vuex from 'vuex';
import Meta from 'vue-meta'
import App from './App.vue';
import {createRouter} from './router';
import createStore from './store';

Vue.use(Vuex);
Vue.use(Meta, {
    ssrAppId: 1 // https://vue-meta.nuxtjs.org/guide/caveats.html#duplicated-tags-after-hydration-with-ssr
});

export const createApp = (context) => {
    const router = createRouter();
    const store = createStore();

    const app = new Vue({
        store,
        router,
        render: h => h(App),
    });

    return {app, router, store};
};

import {createApp} from './app';

export default context => {
    // since there could potentially be asynchronous route hooks or components,
    // we will be returning a Promise so that the server can wait until
    // everything is ready before rendering.
    return new Promise((resolve, reject) => {
        const {app, router, store} = createApp(context);
        // metadata is provided by vue-meta plugin
        const meta = app.$meta();

        // set server-side router's location
        router.push(context.url);

        context.meta = meta;

        // wait until router has resolved possible async components and hooks
        router.onReady(() => {
            const matchedComponents = router.getMatchedComponents();
            // no matched routes, reject with 404
            if (!matchedComponents.length) {
                return reject({code: 404});
            }
            // This `rendered` hook is called when the app has finished rendering
            context.rendered = () => {
                // After the app is rendered, our store is now
                // filled with the state from our components.
                // When we attach the state to the context, and the `template` option
                // is used for the renderer, the state will automatically be
                // serialized and injected into the HTML as `window.__INITIAL_STATE__`.
                context.state = store.state;
            };

            // the Promise should resolve to the app instance so it can be rendered
            resolve(app);
        }, reject);
    })
}
